  return (
    <div className='page'>
      <Nav />
      <div className='Slide'>
        {WorksImages.map((item) => (
          <WorkSlides
            name={item.workName}
            sculp={item.sculpture}
            text={item.text}
            video={item.video}
            key={item.id}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
